ssh ec2-user@$PROD_SERVER "sudo docker exec diffusion-marketplace_app_1 rails runner \"ActiveRecord::Base.logger = nil; year = 2019; month = 1; day = 1; start_time = DateTime.new(year, month, day).beginning_of_day; current_time = DateTime.now(); formatted_start_time = start_time.to_formatted_s(:long_ordinal); formatted_current_time = current_time.to_formatted_s(:long_ordinal); published_innovations_count = Practice.where(published: true).size; puts %Q(Total published innovations - #{published_innovations_count}\n); total_user_accounts = User.where(created_at: start_time..current_time).size; puts %Q(Total user accounts from #{formatted_start_time} UTC - #{formatted_current_time} UTC: #{total_user_accounts}\n); total_innovation_page_views = Practice.published.collect { |p| p.date_range_views(start_time, current_time) }.sum; puts %Q(Total innovation page views from #{formatted_start_time} UTC - #{formatted_current_time} UTC: #{total_innovation_page_views}\n); total_comments = Commontator::Comment.where(created_at: start_time..current_time).size; puts %Q(Total community comments from #{formatted_start_time} UTC - #{formatted_current_time} UTC: #{total_comments}\n); total_bookmarked_innovations = UserPractice.where(time_favorited: start_time..current_time).size; puts %Q(Total bookmarked innovations from #{formatted_start_time} UTC - #{formatted_current_time} UTC: #{total_bookmarked_innovations}); nil; STDOUT.flush\";"